package core_java;

public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int no1=5;
int no2=7;
int no3=35;
int and=no1&no2;
int or=no1|no2;
int xor=no1^no2;
int complement=~no3;
System.out.println("AND:"+and+",OR:"+or+",XOR:"+xor+",COMPLEMENT:"+complement);
	}

}
